import { Divider, Typography, Form, Input, Button, Alert } from "antd";
import { useHistory } from "react-router-dom";
import { Auth } from "aws-amplify";
import { StyledTitle } from "../../Styles/Title.style";
import { StyledContentDiv, StyledDiv } from "../../Styles/Div.style";
import { StyledAuthenticationForm } from "../../Styles/Form.style";
import { useState } from "react";

const { Item } = Form;

const formItemLayout = {
  labelCol: {
    xs: { span: 24 },
    sm: { span: 8 },
  },
  wrapperCol: {
    xs: { span: 24 },
    sm: { span: 16 },
  },
};
const tailFormItemLayout = {
  wrapperCol: {
    xs: {
      span: 24,
      offset: 0,
    },
    sm: {
      span: 16,
      offset: 8,
    },
  },
};

const Verification = () => {
  const [form] = Form.useForm();
  const [showAlert, setShowAlert] = useState(false);
  const history = useHistory();

  const onVerify = async (values: any) => {
    try {
      setShowAlert(false);
      await Auth.confirmSignUp(values.email, values.code);
      history.push("/login");
    } catch (error) {
      setShowAlert(true);
    }
  };

  return (
    <StyledDiv>
      <Typography>
        <StyledTitle level={2}>Verification</StyledTitle>
      </Typography>
      <Divider />
      {showAlert && (
          <Alert
            message="The verification code you have entered is incorrect. Please try again."
            type="error"
            showIcon
            closable
          />
        )}
      <StyledContentDiv>
        <StyledAuthenticationForm
          {...formItemLayout}
          form={form}
          name="register"
          onFinish={onVerify}
          scrollToFirstError
        >
          <Item
            name="email"
            label="E-mail"
            rules={[
              {
                type: "email",
                message: "The input is not a valid email!",
              },
              {
                required: true,
                message: "Please enter your email",
              },
            ]}
          >
            <Input />
          </Item>
          <Item
            name="code"
            label="Verification Code"
            rules={[
              {
                required: true,
                message: "Please enter your verification code",
              },
            ]}
          >
            <Input />
          </Item>
          <Item {...tailFormItemLayout}>
            <Button type="primary" htmlType="submit">
              Confirm
            </Button>
          </Item>
        </StyledAuthenticationForm>
      </StyledContentDiv>
    </StyledDiv>
  );
};

export default Verification;
