import { Divider, Typography, Form, Input, Button } from "antd";
import styled from "styled-components";
import { useHistory } from "react-router-dom";
import { Auth } from "aws-amplify";

const { Title } = Typography;
const { Item } = Form;

const StyledSignUpDiv = styled.div`
  min-height: 100vh;
  padding: 72px;
  background: #fff;

  .signup-title {
    text-align: center;
    margin-bottom: 20px;
  }

  .signup-form {
    width: 500px;
    margin-top: 40px;
  }

  .signup-form-div {
    display: flex;
    justify-content: center;
  }
`;

const formItemLayout = {
  labelCol: {
    xs: { span: 24 },
    sm: { span: 8 },
  },
  wrapperCol: {
    xs: { span: 24 },
    sm: { span: 16 },
  },
};
const tailFormItemLayout = {
  wrapperCol: {
    xs: {
      span: 24,
      offset: 0,
    },
    sm: {
      span: 16,
      offset: 8,
    },
  },
};

const Verification = () => {
  const [form] = Form.useForm();
  const history = useHistory();

  const onVerify = async (values: any) => {
    try {
      await Auth.confirmSignUp(values.email, values.code);
      history.push("/login");
    } catch (error) {
      console.log("error signing up: ", error);
    }
  };

  return (
    <StyledSignUpDiv>
      <Typography>
        <Title className="signup-title" level={2}>
          Verification
        </Title>
      </Typography>
      <Divider />
      <div className="signup-form-div">
        <Form
          {...formItemLayout}
          className="signup-form"
          form={form}
          name="register"
          onFinish={onVerify}
          scrollToFirstError
        >
          <Item
            name="email"
            label="E-mail"
            rules={[
              {
                type: "email",
                message: "The input is not a valid email!",
              },
              {
                required: true,
                message: "Please enter your email",
              },
            ]}
          >
            <Input />
          </Item>
          <Item
            name="code"
            label="Verification Code"
            rules={[
              {
                required: true,
                message: "Please enter your verification code",
              },
            ]}
            {...tailFormItemLayout}
          >
            <Input />
          </Item>
          <Item {...tailFormItemLayout}>
            <Button type="primary" htmlType="submit">
              Confirm
            </Button>
          </Item>
        </Form>
      </div>
    </StyledSignUpDiv>
  );
};

export default Verification;
