import {
  Divider,
  Typography,
  Tabs,
  Row,
  Col,
  Statistic,
  Card,
  Image,
  Spin,
} from "antd";
import unitMedia1 from "../../../images/unitMedia1.jpeg";
import unitMedia2 from "../../../images/unitMedia2.jpeg";
import unitMedia3 from "../../../images/unitMedia3.jpeg";
import unitMedia4 from "../../../images/unitMedia4.jpeg";
import viewsMedia1 from "../../../images/viewsMedia1.jpeg";
import viewsMedia2 from "../../../images/viewsMedia2.jpeg";
import viewsMedia3 from "../../../images/viewsMedia3.jpeg";
import viewsMedia4 from "../../../images/viewsMedia4.jpeg";
import neighbourhoodMedia1 from "../../../images/neighbourhoodMedia1.jpeg";
import neighbourhoodMedia2 from "../../../images/neighbourhoodMedia2.jpeg";
import neighbourhoodMedia3 from "../../../images/neighbourhoodMedia3.jpeg";
import floorplanMedia1 from "../../../images/floorplanMedia1.png";
import floorplanMedia2 from "../../../images/floorplanMedia2.png";
import { Link, useParams } from "react-router-dom";
import {
  StyledDiv,
  StyledLoadingDiv,
  StyledResultBookDiv,
  StyledResultFeesDiv,
  StyledResultGalleryDiv,
} from "../../Styles/Div.style";
import { StyledTitle } from "../../Styles/Title.style";
import { StyledResultParagraph } from "../../Styles/Paragraph.style";
import { StyledResultFeesRow } from "../../Styles/Row.style";
import { StyledResultBookButton } from "../../Styles/Button.style";
import { ArrowRightOutlined } from "@ant-design/icons";
import { useEffect, useState } from "react";
import { API } from "aws-amplify";
import { listUnits } from "../../../graphql/queries";
import { StyledLoadingIcon } from "../../Styles/LoadingOutlined.style";

const { TabPane } = Tabs;
const { PreviewGroup } = Image;

const unitImages = [unitMedia1, unitMedia2, unitMedia3, unitMedia4];
const viewsImages = [viewsMedia1, viewsMedia2, viewsMedia3, viewsMedia4];
const neighbourHoodImages = [
  neighbourhoodMedia1,
  neighbourhoodMedia2,
  neighbourhoodMedia3,
];
const floorplanImages = [floorplanMedia1, floorplanMedia2];

interface Unit {
  unit_number: number;
  price: string;
  square_ft: number;
  beds: number;
  baths: number;
  occupancy_date: number;
}

interface UnitHeaders {
  price: string;
  square_ft: number;
  beds: number;
  baths: number;
  occupancy_date: number;
}

const loadingIcon = <StyledLoadingIcon spin />;

const Result = () => {
  const [unit, setUnit] = useState<Unit>();
  const [unitHeaders, setUnitHeaders] = useState<UnitHeaders>();
  const { id } = useParams<{ id: string }>();

  useEffect(() => {
    getUnit();
  }, []);

  const getUnit = async () => {
    try {
      const filter = {
        id: {
          eq: id,
        },
      };

      const selectedUnit: any = await API.graphql({
        query: listUnits,
        variables: { filter: filter },
      });

      const { unit_number, price, square_ft, beds, baths, occupancy_date } =
        selectedUnit.data.listUnits.items[0];

      const selectedUnitObj: Unit = {
        unit_number: unit_number,
        price: price,
        square_ft: square_ft,
        beds: beds,
        baths: baths,
        occupancy_date: occupancy_date,
      };

      setUnit(selectedUnitObj);

      const selectedUnitHeaders: UnitHeaders = {
        price: price,
        square_ft: square_ft,
        beds: beds,
        baths: baths,
        occupancy_date: occupancy_date,
      };

      setUnitHeaders(selectedUnitHeaders);
    } catch (err) {
      console.log("error getting units: " + err);
    }
  };

  const imagePaneData = [
    {
      tabName: "Renderings",
      images: unitImages,
      index: "2",
    },
    {
      tabName: "Views",
      images: viewsImages,
      index: "3",
    },
    {
      tabName: "Neighbourhood",
      images: neighbourHoodImages,
      index: "4",
    },
    {
      tabName: "Floorplan",
      images: floorplanImages,
      index: "5",
    },
  ];

  const getHeaderName = (key: string) => {
    if (key == "price") {
      return "Price";
    } else if (key == "square_ft") {
      return "Square Foot";
    } else if (key == "beds") {
      return "Beds";
    } else if (key == "baths") {
      return "Baths";
    } else if (key == "occupancy_date") {
      return "Occupancy Date";
    }

    return "";
  };

  console.log(unitHeaders)

  return (
    <StyledDiv>
      {unit && unitHeaders ? (
        <>
          <Typography>
            <StyledTitle level={2}>Unit {unit.unit_number}</StyledTitle>
            <Row>
              {Object.keys(unit).map((key: string, idx: number) => (
                <Col style={{ width: "25%" }}>
                  <StyledResultParagraph type="secondary">
                    {getHeaderName(key)}
                  </StyledResultParagraph>
                  <StyledResultParagraph type="secondary">
                    {unitHeaders[key as keyof UnitHeaders]}
                  </StyledResultParagraph>
                </Col>
              ))}
            </Row>
          </Typography>
          <Divider />
          <StyledResultBookDiv>
            <StyledResultBookButton type="primary" size="large">
              <Link to="/booking">
                Book Now <ArrowRightOutlined />
              </Link>
            </StyledResultBookButton>
          </StyledResultBookDiv>
          <br />
          <Tabs tabPosition="left">
            <TabPane tab="Costs" key="1">
              <StyledResultFeesDiv>
                <StyledResultFeesRow gutter={16}>
                  <Col span={12}>
                    <Card>
                      <Statistic
                        title="Estimated Mortage"
                        value={500000}
                        prefix="$"
                      />
                      <Divider />
                      <Typography>
                        {" "}
                        20% down payment <br /> 25 year amortization <br />{" "}
                        1.75% interest
                      </Typography>
                    </Card>
                  </Col>
                  <Col span={12}>
                    <Card>
                      <Statistic
                        title="Maintenance Fees"
                        value={1000}
                        prefix="$"
                      />
                      <Divider />
                      <Typography>
                        {" "}
                        Min: $0.30/ft <br /> Avg: $0.40/ft <br /> Max: $0.50/ft{" "}
                      </Typography>
                    </Card>
                  </Col>
                </StyledResultFeesRow>
                <Row gutter={16}>
                  <Col span={12}>
                    <Card>
                      <Statistic title="Utility Fees" value={500} prefix="$" />
                      <Divider />
                      <Typography>
                        {" "}
                        Electricity: $200 <br /> Water: $200 <br /> Internet:
                        $100{" "}
                      </Typography>
                    </Card>
                  </Col>
                  <Col span={12}>
                    <Card>
                      <Statistic title="Taxes" value={750} prefix="$" />
                      <Divider />
                      <Typography>
                        {" "}
                        GST: $300 <br /> HST: $200 <br /> PST: $250{" "}
                      </Typography>
                    </Card>
                  </Col>
                </Row>
              </StyledResultFeesDiv>
            </TabPane>
            {imagePaneData.map((pane, index) => {
              return (
                <TabPane
                  tab={imagePaneData[index].tabName}
                  key={imagePaneData[index].index}
                >
                  <StyledResultGalleryDiv>
                    <PreviewGroup>
                      {imagePaneData[index].images.map(
                        (image: string, idx: number) => {
                          return (
                            <div>
                              <Image key={idx} src={image} />
                            </div>
                          );
                        }
                      )}
                    </PreviewGroup>
                  </StyledResultGalleryDiv>
                </TabPane>
              );
            })}
          </Tabs>
        </>
      ) : (
        <StyledLoadingDiv>
          <Spin indicator={loadingIcon} size="large" />
        </StyledLoadingDiv>
      )}
    </StyledDiv>
  );
};

export default Result;
