import {
  Divider,
  Typography,
  Tabs,
  Row,
  Col,
  Statistic,
  Card,
  Image,
  Spin,
} from "antd";
import unitMedia1 from "../../../images/unitMedia1.jpeg";
import unitMedia2 from "../../../images/unitMedia2.jpeg";
import unitMedia3 from "../../../images/unitMedia3.jpeg";
import unitMedia4 from "../../../images/unitMedia4.jpeg";
import viewsMedia1 from "../../../images/viewsMedia1.jpeg";
import viewsMedia2 from "../../../images/viewsMedia2.jpeg";
import viewsMedia3 from "../../../images/viewsMedia3.jpeg";
import viewsMedia4 from "../../../images/viewsMedia4.jpeg";
import neighbourhoodMedia1 from "../../../images/neighbourhoodMedia1.jpeg";
import neighbourhoodMedia2 from "../../../images/neighbourhoodMedia2.jpeg";
import neighbourhoodMedia3 from "../../../images/neighbourhoodMedia3.jpeg";
import floorplanCielo from "../../../images/floorplanCielo.png";
import keyplateCielo from "../../../images/keyplateCielo.png";
import { Storage } from "aws-amplify";
import { Link, useParams } from "react-router-dom";
import {
  StyledDiv,
  StyledLoadingDiv,
  StyledResultBookDiv,
  StyledResultFeesDiv,
  StyledResultGalleryDiv,
} from "../../Styles/Div.style";
import { StyledTitle } from "../../Styles/Title.style";
import { StyledResultParagraph } from "../../Styles/Paragraph.style";
import { StyledResultFeesRow } from "../../Styles/Row.style";
import { StyledResultBookButton } from "../../Styles/Button.style";
import { ArrowRightOutlined } from "@ant-design/icons";
import { useEffect, useState } from "react";
import { API } from "aws-amplify";
import { listUnits } from "../../../graphql/queries";
import { StyledLoadingIcon } from "../../Styles/LoadingOutlined.style";

const { TabPane } = Tabs;
const { PreviewGroup } = Image;

interface Unit {
  collection: string;
  unit_number: number;
  price: string;
  square_ft: number;
  beds: number;
  baths: number;
  occupancy_date: number;
}

interface UnitHeaders {
  unit_number: number;
  price: string;
  square_ft: number;
  beds: number;
  baths: number;
  occupancy_date: number;
}

const loadingIcon = <StyledLoadingIcon spin />;

const Result = () => {
  const [unit, setUnit] = useState<Unit>();
  const [unitHeaders, setUnitHeaders] = useState<UnitHeaders>();
  const [renderingsImageLinks, setRenderingsImageLinks] = useState([""]);
  const [viewsImageLinks, setViewsImageLinks] = useState([""]);
  const [neighbourhoodImageLinks, setNeighbourhoodImageLinks] = useState([""]);
  const [floorplanImageLinks, setFloorplanImageLinks] = useState([""]);
  const { id } = useParams<{ id: string }>();

  const unitImages = [unitMedia1, unitMedia2, unitMedia3, unitMedia4];
  const viewsImages = [viewsMedia1, viewsMedia2, viewsMedia3, viewsMedia4];
  const floorplanImages = [floorplanCielo, keyplateCielo];

  useEffect(() => {
    getUnit();
    getRenderingImages();
    getViewsImages();
    getNeighbourhoodImages();
    getFloorplanImages();
  }, []);

  const getUnit = async () => {
    try {
      const filter = {
        id: {
          eq: id,
        },
      };

      const selectedUnit: any = await API.graphql({
        query: listUnits,
        variables: { filter: filter },
      });

      const {
        collection,
        unit_number,
        price,
        square_ft,
        beds,
        baths,
        occupancy_date,
      } = selectedUnit.data.listUnits.items[0];

      const selectedUnitObj: Unit = {
        collection: collection,
        unit_number: unit_number,
        price: price,
        square_ft: square_ft,
        beds: beds,
        baths: baths,
        occupancy_date: occupancy_date,
      };

      setUnit(selectedUnitObj);

      const selectedUnitHeaders: UnitHeaders = {
        unit_number: unit_number,
        price: price,
        square_ft: square_ft,
        beds: beds,
        baths: baths,
        occupancy_date: occupancy_date,
      };

      setUnitHeaders(selectedUnitHeaders);
    } catch (err) {
      console.log("error getting units: " + err);
    }
  };

  const getRenderingImages = async () => {
    try {
      const renderingsImages = await Storage.list("renderings/");
      const renderingsImagesKeys = renderingsImages
        .slice(1, renderingsImages.length - 1)
        .map((image) => {
          return image.key;
        });
      console.log(renderingsImagesKeys);

      const renderingsImageLinks = [];

      for (const imageKey of renderingsImagesKeys) {
        if (imageKey) {
          const fileAccessURL = await Storage.get(imageKey);

          renderingsImageLinks.push(fileAccessURL);
        }
      }

      setRenderingsImageLinks(renderingsImageLinks);
    } catch (err) {
      console.log("error accessing the file from S3: " + err);
    }
  };

  const getViewsImages = async () => {
    try {
      const viewsImages = await Storage.list("views/");
      const viewsImagesKeys = viewsImages
        .slice(1, viewsImages.length - 1)
        .map((image) => {
          return image.key;
        });
      console.log(viewsImagesKeys);

      const viewsImageLinks = [];

      for (const imageKey of viewsImagesKeys) {
        if (imageKey) {
          const fileAccessURL = await Storage.get(imageKey);

          viewsImageLinks.push(fileAccessURL);
        }
      }

      setViewsImageLinks(viewsImageLinks);
    } catch (err) {
      console.log("error accessing the file from S3: " + err);
    }
  };

  const getNeighbourhoodImages = async () => {
    try {
      const neighbourhoodImages = await Storage.list("neighbourhood/");
      const neighbourhoodImagesKeys = neighbourhoodImages
