import {
  LoadingOutlined,
  PlusOutlined,
  RightOutlined
} from "@ant-design/icons";
import { Button, message, Space, Typography } from "antd";
import { useContext, useState } from "react";
import { BookingStatusContext } from "../../../utils/BookingState";
import {
  StyledButtonNavDiv,
  StyledContentDiv,
  StyledIdentificationDiv,
  StyledIdentificationUploadDiv
} from "../../Styles/Div.style";
import { StyledIdentificationUploadImage } from "../../Styles/Image.style";
import { StyledPrompt } from "../../Styles/Title.style";
import { StyledIdentificationUploader } from "../../Styles/Upload.style";

const getBase64 = (
  img: Blob,
  callback: { (imageUrl: any): void; (arg0: string | ArrayBuffer | null): any }
) => {
  const reader = new FileReader();
  reader.addEventListener("load", () => callback(reader.result));
  reader.readAsDataURL(img);
};

const beforeUpload = (file: { type: string; size: number }) => {
  const isJpgOrPng = file.type === "image/jpeg" || file.type === "image/png";

  if (!isJpgOrPng) {
    message.error("Please upload a JPG/PNG file");
  }

  const isLt2M = file.size / 1024 / 1024 < 2;

  if (!isLt2M) {
    message.error("Image must smaller than 2MB");
  }

  return isJpgOrPng && isLt2M;
};

const Identification = () => {
  const { currentStep, setCurrentStep } = useContext(BookingStatusContext);
  const [loading, setLoading] = useState<boolean>(false);
  const [imageUrl, setImageUrl] = useState<string>("");

  const handleChange = (info: {
    file: { status: string; originFileObj: Blob };
  }) => {
    console.log(info)
    if (info.file.status === "uploading") {
      setLoading(true);
      return;
    }

    if (info.file.status === "done") {
      // Get this url from response in real world.
      getBase64(info.file.originFileObj, (imageUrl) => {
        setImageUrl(imageUrl);
        setLoading(false);
      });
    }
  };

  const uploadButton = (
    <div>
      {loading ? <LoadingOutlined /> : <PlusOutlined />}
      <StyledIdentificationUploadDiv>Upload</StyledIdentificationUploadDiv>
    </div>
  );

  return (
    <StyledIdentificationDiv>
      <Typography>
        <StyledPrompt level={5}>
          Please upload a photo of your drivers license
        </StyledPrompt>
      </Typography>
      <StyledContentDiv>
        <StyledIdentificationUploader
          name="avatar"
          listType="picture-card"
          showUploadList={false}
          action="https://www.mocky.io/v2/5cc8019d300000980a055e76"
          beforeUpload={beforeUpload}
          onChange={() => handleChange}
        >
          {imageUrl ? (
            <StyledIdentificationUploadImage src={imageUrl} alt="avatar" />
          ) : (
            uploadButton
          )}
        </StyledIdentificationUploader>
      </StyledContentDiv>
      <br />
      <br />
      <StyledButtonNavDiv>
        <Space>
          <Button
            type="primary"
            shape="circle"
            size="large"
            icon={<RightOutlined />}
            onClick={() => {
              setCurrentStep(currentStep + 1);
            }}
          />
        </Space>
      </StyledButtonNavDiv>
    </StyledIdentificationDiv>
  );
};

export default Identification;
