import {
  Divider,
  Typography,
  Form,
  Input,
  Button,
  Checkbox,
  Select,
} from "antd";
import styled from "styled-components";
import { useHistory } from "react-router-dom";
import { Auth } from "aws-amplify";

const { Title } = Typography;
const { Option } = Select;
const { Item } = Form;

const StyledSignUpDiv = styled.div`
  min-height: 100vh;
  padding: 72px;
  background: #fff;

  .signup-title {
    text-align: center;
    margin-bottom: 20px;
  }

  .signup-form {
    width: 500px;
    margin-top: 40px;
  }

  .signup-form-div {
    display: flex;
    justify-content: center;
  }
`;

const formItemLayout = {
  labelCol: {
    xs: { span: 24 },
    sm: { span: 8 },
  },
  wrapperCol: {
    xs: { span: 24 },
    sm: { span: 16 },
  },
};
const tailFormItemLayout = {
  wrapperCol: {
    xs: {
      span: 24,
      offset: 0,
    },
    sm: {
      span: 16,
      offset: 8,
    },
  },
};

const SignUp = () => {
  const [form] = Form.useForm();
  const history = useHistory();

  const onSignUp = async (values: any) => {
    try {
      await Auth.signUp({
        username: values.email,
        password: values.password,
        attributes: {
          given_name: values.givenName,
          family_name: values.familyName,
          gender: values.gender,
          phone_number: values.phoneNumber,
        },
      });
      history.push("/login");
    } catch (error) {
      console.log("error signing up: ", error);
    }
  };

  return (
    <StyledSignUpDiv>
      <Typography>
        <Title className="signup-title" level={2}>
          Sign Up
        </Title>
      </Typography>
      <Divider />
      <div className="signup-form-div">
        <Form
          {...formItemLayout}
          className="signup-form"
          form={form}
          name="register"
          onFinish={onSignUp}
          scrollToFirstError
        >
          <Item
            name="givenName"
            label="Given Name"
            rules={[
              {
                required: true,
                message: "Please enter your given name",
              },
            ]}
          >
            <Input />
          </Item>
          <Item
            name="familyName"
            label="Family Name"
            rules={[
              {
                required: true,
                message: "Please enter your family name",
              },
            ]}
          >
            <Input />
          </Item>
          <Item
            name="email"
            label="E-mail"
            rules={[
              {
                type: "email",
                message: "The input is not a valid email!",
              },
              {
                required: true,
                message: "Please enter your email",
              },
            ]}
          >
            <Input />
          </Item>
          <Item
            name="password"
            label="Password"
            rules={[
              {
                required: true,
                message: "Please enter your password",
              },
            ]}
            hasFeedback
          >
            <Input.Password />
          </Item>
          <Item
            name="confirm"
            label="Confirm Password"
            dependencies={["password"]}
            hasFeedback
            rules={[
              {
                required: true,
                message: "Please confirm your password",
              },
              ({ getFieldValue }) => ({
                validator(_, value) {
                  if (!value || getFieldValue("password") === value) {
                    return Promise.resolve();
                  }
                  return Promise.reject(
                    new Error("The passwords you have entered do not match")
                  );
                },
              }),
            ]}
          >
            <Input.Password />
          </Item>
          <Item
            name="phoneNumber"
            label="Phone Number"
            rules={[
              { required: true, message: "Please enter your phone number" },
              ({ getFieldValue }) => ({
                validator(_, value) {
                  if (
                    !value ||
                    (getFieldValue("phone").length === 10 &&
                      !isNaN(getFieldValue("phone")) &&
                      !isNaN(parseFloat(getFieldValue("phone"))))
                  ) {
                    return Promise.resolve();
                  }
                  return Promise.reject(
                    new Error("The phone number you have entered is invalid")
                  );
                },
              }),
            ]}
          >
            <Input style={{ width: "100%" }} />
          </Item>

          <Item
            name="gender"
            label="Gender"
            rules={[{ required: true, message: "Please select your gender" }]}
          >
            <Select placeholder="Select your gender">
              <Option value="male">Male</Option>
              <Option value="female">Female</Option>
              <Option value="other">Other</Option>
            </Select>
          </Item>
          <Item
            name="agreement"
            valuePropName="checked"
            rules={[
              {
                validator: (_, value) =>
                  value
                    ? Promise.resolve()
                    : Promise.reject(new Error("Please accept the agreement")),
              },
            ]}
            {...tailFormItemLayout}
          >
            <Checkbox>
              I have read the <a href="/">agreement</a>
            </Checkbox>
          </Item>
          <Item {...tailFormItemLayout}>
            <Button type="primary" htmlType="submit">
              Register
            </Button>
          </Item>
        </Form>
      </div>
    </StyledSignUpDiv>
  );
};

export default SignUp;
