import { Divider, Typography, Form, Input, Button } from "antd";
import { useHistory } from "react-router-dom";
import { Auth } from "aws-amplify";
import { StyledTitle } from "../../Styles/Title.style";
import { StyledContentDiv, StyledDiv } from "../../Styles/Div.style";
import { StyledAuthenticationForm } from "../../Styles/Form.style";

const { Item } = Form;

const formItemLayout = {
  labelCol: {
    xs: { span: 24 },
    sm: { span: 8 },
  },
  wrapperCol: {
    xs: { span: 24 },
    sm: { span: 16 },
  },
};
const tailFormItemLayout = {
  wrapperCol: {
    xs: {
      span: 24,
      offset: 0,
    },
    sm: {
      span: 16,
      offset: 8,
    },
  },
};

const Reset = () => {
  const [form] = Form.useForm();
  const history = useHistory();

  const onVerify = async (values: any) => {
    try {
      await Auth.forgotPasswordSubmit(values.email, values.code, values.password);
      history.push("/login");
    } catch (error) {
      console.log("error verifying: ", error);
    }
  };

  return (
    <StyledDiv>
      <Typography>
        <StyledTitle level={2}>Reset Password</StyledTitle>
      </Typography>
      <Divider />
      <StyledContentDiv>
        <StyledAuthenticationForm
          {...formItemLayout}
          form={form}
          name="register"
          onFinish={onVerify}
          scrollToFirstError
        >
          <Item
            name="email"
            label="E-mail"
            rules={[
              {
                type: "email",
                message: "The input is not a valid email!",
              },
              {
                required: true,
                message: "Please enter your email",
              },
            ]}
          >
            <Input />
          </Item>
          <Item
            name="code"
            label="Verification Code"
            rules={[
              {
                required: true,
                message: "Please enter your verification code",
              },
            ]}
          >
            <Input />
          </Item>
          <Item
            name="password"
            label="New Password"
            rules={[
              {
                required: true,
                message: "Please enter your new password",
              },
            ]}
          >
            <Input />
          </Item>
          <Item {...tailFormItemLayout}>
            <Button type="primary" htmlType="submit">
              Confirm
            </Button>
          </Item>
        </StyledAuthenticationForm>
      </StyledContentDiv>
    </StyledDiv>
  );
};

export default Reset;
