import { FilterOutlined } from "@ant-design/icons";
import { Divider, Table, Typography, message } from "antd";
import { FormContext } from "../../utils/FormState";
import { useContext, useEffect, useState } from "react";
import {
  BathroomsKind,
  BedroomsKind,
  BudgetKind,
  OccupancyKind,
  SqftKind,
} from "../../utils/enums";
import { useHistory } from "react-router-dom";
import { StyledSubtitle, StyledTitle } from "../Styles/Title.style";
import { StyledDiv } from "../Styles/Div.style";
import API, { graphqlOperation } from "@aws-amplify/api";
import { listUnits } from "../../graphql/queries";

const { Column } = Table;
interface Unit {
  id: string;
  unit: number;
  price: string;
  sqft: number;
  beds: number;
  baths: number;
  occupancy: number;
}

const units: Unit[] = [
  {
    id: "1",
    unit: 1,
    price: "2500000".replace(new RegExp(/\B(?=(\d{3})+(?!\d))/g), ","),
    sqft: 1500,
    beds: 2,
    baths: 2,
    occupancy: 2022,
  },
  {
    id: "2",
    unit: 2,
    price: "3500000".replace(new RegExp(/\B(?=(\d{3})+(?!\d))/g), ","),
    sqft: 2500,
    beds: 2,
    baths: 3,
    occupancy: 2023,
  },
  {
    id: "3",
    unit: 3,
    price: "500000".replace(new RegExp(/\B(?=(\d{3})+(?!\d))/g), ","),
    sqft: 1250,
    beds: 1,
    baths: 1,
    occupancy: 2024,
  },
];

const Results = () => {
  const [filteredUnits, setFilteredUnits] = useState([]);
  const { formState } = useContext(FormContext);
  const history = useHistory();

  useEffect(() => {
    message.info({
      content: "Tip: Click on the filter icon to narrow your results",
      icon: <FilterOutlined />,
      duration: 5,
    });

    getUnits();
  }, []);

  const getUnits = async () => {
    try {
      const units: any = await API.graphql(graphqlOperation(listUnits));
      setFilteredUnits(units.data.listUnits.items);
    } catch (err) {
      console.log(err);
    }
  };

  const getDefaultNumBedrooms = () => {
    const numBedrooms = formState.numBedrooms.map((bedroom: number) => {
      switch (bedroom) {
        case BedroomsKind.BACHELOR:
          return BedroomsKind.BACHELOR.toString();
        case BedroomsKind.ONE_BED:
          return BedroomsKind.ONE_BED.toString();
        case BedroomsKind.ONE_BED_PLUS_DEN:
          return BedroomsKind.ONE_BED_PLUS_DEN.toString();
        case BedroomsKind.TWO_BED:
          return BedroomsKind.TWO_BED.toString();
        case BedroomsKind.TWO_BED_PLUS_DEN:
          return BedroomsKind.TWO_BED_PLUS_DEN.toString();
        default:
          return "";
      }
    });

    return numBedrooms;
  };

  const getDefaultNumBathrooms = () => {
    const numBathrooms = formState.numBathrooms.map((bathroom: number) => {
      switch (bathroom) {
        case BathroomsKind.ONE_BATH:
          return BathroomsKind.ONE_BATH.toString();
        case BathroomsKind.TWO_BATH:
          return BathroomsKind.TWO_BATH.toString();
        case BathroomsKind.THREE_BATH:
          return BathroomsKind.THREE_BATH.toString();
        default:
          return "";
      }
    });

    return numBathrooms;
  };

  const getDefaultPrice = () => {
    const prices = [
      BudgetKind.ZERO,
      BudgetKind.ONE_MILLION,
      BudgetKind.TWO_MILLION,
      BudgetKind.THREE_MILLION,
      BudgetKind.FOUR_MILLION,
    ]
      .filter((price) => {
        return price >= formState.budget[0] && price < formState.budget[1];
      })
      .map((price) => {
        return price.toString();
      });

    return prices;
  };

  console.log(filteredUnits);

  return (
    <StyledDiv>
      <Typography>
        <StyledTitle level={2}>Property Finder</StyledTitle>
        <StyledSubtitle level={5} type="secondary">
          A list of our available properties
        </StyledSubtitle>
      </Typography>
      <Divider />
      <Table<Unit>
        style={{ cursor: "pointer" }}
        dataSource={units}
        pagination={{ pageSize: 25 }}
        scroll={{ y: 350 }}
        onRow={(record) => {
          return {
            onClick: () => history.push("result"),
          };
        }}
      >
        <Column<Unit> title="Unit" dataIndex="unit" />
        <Column<Unit>
          title="SQFT"
          dataIndex="sqft"
          filters={[
            {
              text: "0 - 1K",
              value: SqftKind.ZERO,
            },
            {
              text: "1K - 2K",
              value: SqftKind.ONE_THOUSAND,
            },
            {
              text: "2K - 3K",
              value: SqftKind.TWO_THOUSAND,
            },
            {
              text: "3K - 4K",
              value: SqftKind.THREE_THOUSAND,
            },
            {
              text: "4K - 5K",
              value: SqftKind.FOUR_THOUSAND,
            },
          ]}
          sorter={(a, b) => a.sqft - b.sqft}
          onFilter={(value, record) => {
            return record.sqft >= value && record.sqft < Number(value) + 1000;
          }}
        />
        <Column<Unit>
          title="Beds"
          dataIndex="beds"
          filters={[
            {
              text: "Bachelor",
              value: BedroomsKind.BACHELOR,
            },
            {
              text: "1",
              value: BedroomsKind.ONE_BED,
            },
            {
              text: "1 + Den",
              value: BedroomsKind.ONE_BED_PLUS_DEN,
            },
            {
              text: "2",
              value: BedroomsKind.TWO_BED,
            },
            {
              text: "2 + Den",
              value: BedroomsKind.TWO_BED_PLUS_DEN,
            },
          ]}
          sorter={(a, b) => a.beds - b.beds}
          defaultFilteredValue={getDefaultNumBedrooms()}
          onFilter={(value, record) => {
            return record.beds.toString().indexOf(value.toString()) === 0;
          }}
        />
        <Column<Unit>
          title="Baths"
          dataIndex="baths"
          filters={[
            {
              text: "1",
              value: BathroomsKind.ONE_BATH,
            },
            {
              text: "2",
              value: BathroomsKind.TWO_BATH,
            },
            {
              text: "3",
              value: BathroomsKind.THREE_BATH,
            },
          ]}
          sorter={(a, b) => a.baths - b.baths}
          defaultFilteredValue={getDefaultNumBathrooms()}
          onFilter={(value, record) => {
            return record.baths.toString().indexOf(value.toString()) === 0;
          }}
        />
        <Column<Unit>
          title="Price"
          dataIndex="price"
          filters={[
            {
              text: "$0 - $1M",
              value: BudgetKind.ZERO,
            },
            {
              text: "$1M - $2M",
              value: BudgetKind.ONE_MILLION,
            },
            {
              text: "$2M - $3M",
              value: BudgetKind.TWO_MILLION,
            },
            {
              text: "$3M - $4M",
              value: BudgetKind.THREE_MILLION,
            },
            {
              text: "$4M - $5M",
              value: BudgetKind.FOUR_MILLION,
            },
          ]}
          sorter={(a, b) =>
            parseInt(a.price.replace(/,/g, "")) -
            parseInt(b.price.replace(/,/g, ""))
          }
          defaultFilteredValue={getDefaultPrice()}
          onFilter={(value, record) => {
            return (
              parseInt(record.price.replace(/,/g, "")) > value &&
              parseInt(record.price.replace(/,/g, "")) <
                Number(value) + BudgetKind.ONE_MILLION
            );
          }}
        />
        <Column<Unit>
          key="occupancy"
          title="Occupancy"
          dataIndex="occupancy"
          filters={[
            {
              text: "2022",
              value: OccupancyKind.TWO_THOUSAND_AND_TWENTY_TWO,
            },
            {
              text: "2023",
              value: OccupancyKind.TWO_THOUSAND_AND_TWENTY_THREE,
            },
            {
              text: "2024",
              value: OccupancyKind.TWO_THOUSAND_AND_TWENTY_FOUR,
            },
          ]}
          sorter={(a, b) => a.occupancy - b.occupancy}
        />
      </Table>
    </StyledDiv>
  );
};

export default Results;
